import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {

  constructor() {
    super();
    console.log("ejdbnewjk");
    this.state = {
      articles: [],
      loading: false
    }
  }

  // componentDidMount
  async componentDidMount() {
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=3433bbf108ca41b1bbc25bec3311389f";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ articles: parsedData.articles });
  }

  render() {
    return (
      <>
        <div className='container my-5'>
          <h1>KenNews - Top Headlines</h1>
          <div className='row mt-4'>
            {this.state.articles.map((element) => {

              const { title, description, urlToImage, url } = element;

              if (title && description && urlToImage) {
                return (
                  <div className='col-4 mb-5' key={url}>
                    <NewsItem
                      title={title.slice(0, 60)}
                      description={description.slice(0, 100)}
                      imgUrl={urlToImage}
                      newsurl={url}
                    />
                  </div>
                );
              } else {
                return null;
              }
            })}

          </div>
        </div>
        <div className='d-flex justify-context-between'>
            <button className="btn btn-dark"> &larr; Previous</button>
            <button className="btn btn-dark"> &rarr; Next</button>
        </div>
      </>

    )
  }
}

export default News
