import React, { Component } from 'react';

export default class data extends Component {
  state = {
    data: null,
    error: null,
  };

  componentDidMount() {
    // Perform the GET request when the component mounts
    fetch('https://newsapi.org/v2/top-headlines?country=in&apiKey=386c721da11e4522a3b2771e2cbdd8ee}') // Replace 'YOUR_API_URL' with the actual API endpoint
      .then((response) => response.json())
      .then((data) => {
        this.setState({ data });
      })
      .catch((error) => {
        this.setState({ error: 'Failed to fetch data' });
      });
  }

  render() {
    const { data, error } = this.state;

    return (
      <div>
        {error && <p>{error}</p>}
        {data && (
          <ul>
            {data.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}
