import './App.css';

import React, { Component } from 'react'
import Navbar from './Components/Navbar';
import News from './Components/News';
import {
  BrowserRouter as Router,
  Route,
  Routes
} from "react-router-dom";

export default class App extends Component {

  // constructor
  constructor() {
    super();
    this.state = {
      mode: 'dark',
    }
  }

  // component did mount 
  async componentDidMount() {
    await this.defaultModeChange();
    await this.toggleModeFun();
  }
  async defaultModeChange() {
    if (this.state.mode === 'dark') {
      console.log("dark");
      document.body.style.backgroundColor = 'black';
    } else {
      console.log("white");
      document.body.style.backgroundColor = 'black'; // Should this be 'white' instead of 'black'?
    }
  }
  async toggleModeFun() {
    if (this.state.mode === "light") {
      this.setState({
        mode: "dark",
      });
      document.body.style.backgroundColor = 'black';
    }
    else {
      this.setState({
        mode: "light",
      });
      document.body.style.backgroundColor = 'white';
    }
  }

  render() {
    return (
      <>
        <div>
          <Router>
            <Navbar mode={this.state.mode} toggleMode={this.toggleModeFun} />
            <Routes>
              <Route exact path="/" element={<News key="general" country="in" category="general" />} />
              <Route exact path="/business" element={<News key="business" country="in" category="business" />} />
              <Route exact path="/entertainment" element={<News key="entertainment" country="in" category="entertainment" />} />
              <Route exact path="/health" element={<News key="health" country="in" category="health" />} />
              <Route exact path="/sports" element={<News key="sports" country="in" category="sports" />} />
              <Route exact path="/science" element={<News key="science" country="in" category="science" />} />
              <Route exact path="/technology" element={<News key="technology" country="in" category="technology" />} />
            </Routes>
          </Router>
        </div>
      </>
    )
  }
}
