import './App.css';

import React, { Component } from 'react'
import Navbar from './Components/Navbar';
import News from './Components/News';
import {
  BrowserRouter as Router,
  Route,
  Routes
} from "react-router-dom";

export default class App extends Component {

  // constructor
  constructor(props) {
    super(props);
    this.state = {
      mode: 'dark',
    };
    document.title = `${this.props.category}`;
    this.toggleModeFun = this.toggleModeFun.bind(this);
  }

  // component did mount 
  async componentDidMount() {
    await this.defaultModeChange();
  }
  async defaultModeChange() {
    if (this.state.mode === 'dark') {
      document.body.style.backgroundColor = 'black';
    } else {
      document.body.style.backgroundColor = 'white'; // Should this be 'white' instead of 'black'?
    }
  }
  async toggleModeFun() {

    if (this.state.mode === "light") {
      this.setState({ mode: 'dark' }, this.defaultModeChange);
    }
    else {
      this.setState({ mode: 'light' }, this.defaultModeChange);
    }
  }

  render() {
    return (
      <>
        <div>
          <Router>
            <Navbar mode={this.state.mode} toggleMode={this.toggleModeFun} />
            <Routes>
              <Route exact path="/" element={<News key="general" country="in" category="general" mode={this.state.mode} />} />
              <Route exact path="/business" element={<News key="business" country="in" category="business" mode={this.state.mode} />} />
              <Route exact path="/entertainment" element={<News key="entertainment" country="in" category="entertainment" mode={this.state.mode} />} />
              <Route exact path="/health" element={<News key="health" country="in" category="health" mode={this.state.mode} />} />
              <Route exact path="/sports" element={<News key="sports" country="in" category="sports" mode={this.state.mode} />} />
              <Route exact path="/science" element={<News key="science" country="in" category="science" mode={this.state.mode} />} />
              <Route exact path="/technology" element={<News key="technology" country="in" category="technology" mode={this.state.mode} />} />
            </Routes>
          </Router>
        </div>
      </>
    )
  }
}
