import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  articles = []

  constructor() {
    super();
    console.log("ejdbnewjk");
    this.state = {
      articles: this.articles,
      loading: false
    }
  }

  // componentDidMount
  async componentDidMount() {
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=3433bbf108ca41b1bbc25bec3311389f";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ articles: parsedData.articles });
  }

  render() {
    return (
      <div className='container my-3'>
        <h3>TV9News - Top Headlines</h3>
        <div className='row mt-4'>
          {this.state.articles.map((element) => {
            
            const { title, description, urlToImage, url } = element;

            if (title && description && urlToImage) {
              return (
                <div className='col-4 mb-5' key={url}>
                  <NewsItem
                    title={title.slice(0, 60)}
                    description={description.slice(0, 100)}
                    imgUrl={!urlToImage ? urlToImage: "https://images.hindustantimes.com/tech/img/2023/07/18/1600x900/pexels-lisa-fotios-1092644_1689659627064_1689659636635.jpg"}
                    newsurl={url}
                  />
                </div>
              );
            } else {
              return null;
            }
          })}

        </div>
      </div>
    )
  }
}

export default News
