  //   // Filter Articles
  //   let filteredArticles = parsedData.articles.filter(
  //     (article) => article.title && article.description && article.urlToImage
  //   );

  //   console.log(filteredArticles);
  //   console.log(this.state.totalResult );

  //   this.setState({
  //     articles: filteredArticles,
  //     totalResult: parsedData.totalResults,
  //     loading: false,
  //   });
  // }

  // // redirect Previous page
  // movePreviousPage = async () => {

  //   if (this.state.pageNo > 1) {
  //     console.log(this.state.totalResult );
  //     this.setState({ pageNo: this.state.pageNo - 1 }, async () => {
  //       await this.fetchArticles();
  //     });
  //   }
  // }
  // // redirect Next page
  // moveNextPage = async () => {

  //   if (!(this.state.pageNo + 1 > Math.ceil(this.state.totalResult / this.state.pageSize))) {
  //     console.log(this.state.totalResult );
  //     this.setState({ pageNo: this.state.pageNo + 1 }, async () => {
  //       await this.fetchArticles();
  //     });
  //   }
  // }
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      pageNo: 1,
      pageSize: 9,
      totalResult: 0,
    };
  }

  async componentDidMount() {
    await this.fetchData();
  }

  async fetchData() {
    this.setState({ loading: true });
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3433bbf108ca41b1bbc25bec3311389f&page=${this.state.pageNo}&pageSize=${this.state.pageSize + 3}`;
    let data = await fetch(url);
    let parsedData = await data.json();

    // Filter out articles with null title, description, or image
    let filteredArticles = parsedData.articles.filter(
      (article) => article.title && article.description && article.urlToImage
    );

    // Ensure the correct number of articles (pageSize) are shown on the page
    let articlesForPage = filteredArticles.slice(0, this.state.pageSize);

    this.setState({
      articles: articlesForPage,
      totalResult: parsedData.totalResults,
      loading: false,
    });
  }

  // Redirect to Previous page
  movePreviousPage = async () => {
    if (this.state.pageNo > 1) {
      this.setState({ pageNo: this.state.pageNo - 1 }, async () => {
        await this.fetchData();
      });
    }
  };

  // Redirect to Next page
  moveNextPage = async () => {
    if (!(this.state.pageNo + 1 > Math.ceil(this.state.totalResult / this.state.pageSize))) {
      this.setState({ pageNo: this.state.pageNo + 1 }, async () => {
        await this.fetchData();
      });
    }
  };

  render() {
    return (
      <>
        <div className='container my-5'>
          <h1>KenNews - Top Headlines</h1>
          {this.state.loading && <Spinner />}
          <div className='row mt-4'>
            {!this.state.loading && this.state.articles.map((element) => {

              const { title, description, urlToImage, url } = element;

              if (title && description && urlToImage) {
                return (
                  <div className='col-4 mb-5' key={url}>
                    <NewsItem
                      title={title.slice(0, 60)}
                      description={description.slice(0, 100)}
                      imgUrl={urlToImage}
                      newsurl={url}
                    />
                  </div>
                );
              } else {
                return null;
              }
            })}
          </div>
          <div className='d-flex justify-content-between'>
            <button className="btn btn-dark" disabled={this.state.pageNo <= 1} onClick={this.movePreviousPage}> &larr; Previous</button>
            <button className="btn btn-dark" disabled={this.state.pageNo + 1 > Math.ceil(this.state.totalResult / this.state.pageSize)} onClick={this.moveNextPage}> &rarr; Next</button>
          </div>
        </div>
      </>

    )
  }
}

export default News
