import React, { Component } from 'react'

export class NewsItem extends Component {


  render() {
    let { title, description, imgUrl, newsurl, mode } = this.props;
    return (
      <div>
        <div className="card card-style" style={`{border:{mode === 'dark ? '2px solid white' : 'spx solid black}}`}>
          <img src={imgUrl} className="card-img-top imagecss" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{title}...</h5>
            <p className="card-text">{description}...</p>
          </div>
          <div className='text-start' style={{padding : '0px 0px 16px 16px'}}>
            <a href={newsurl} target='blank' className="btn btn-dark">Read More</a>
          </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
