            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
        </ul>
        <Alertcom alert={alert} />
        <Switch>
          <Route path="/about">
            <Aboutus />
          </Route>
          <Route path="/">
            <TextForm mode={mode} />
          </Route>
        </Switch>
        <TextForm mode={mode} />
      </Router> */}
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
            </ul>
          </nav>

          <Routes>
            <Route exact path="/">
              <Navbar title="Text Editor" home="Home" aboutUs="About Us" mode={mode} toggleMode={toggleModeFun} />
            </Route>
            <Route path="/about">
              <Aboutus />
            </Route>
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
