import React, { useState, useReducer } from 'react'

export default function TextForm() {

    const updateText = (event) => {
        setText(event.target.value);
        setCopyText("Copy");
    }

    const convertUpper = () => {

        let newtext = text.toUpperCase().replace(/\s+/g, " ");
        setText(newtext);
        setCopyText("Copy");

    }
    const convertLower = () => {
        let newtext = text.toLowerCase().replace(/\s+/g, " ");
        setText(newtext);
        setCopyText("Copy");
    }

    const removeSpace = () => {
        // Global regular expression
        const newText = text.replace(/\s+/g, " ");
        setText(newText);
        setCopyText("Copy");
    }
    const reverseText = () => {
        const newText = text.split(" ").reverse().join(" ").replace(/\s+/g, " ");
        setText(newText);
        setCopyText("Copy");
    }

    const clearText = () => {
        setText('');
        setCopyText("Copy");
    }

    // Textarea set text State
    const [text, setText] = useState(""); // State used this
    // text = "change text";   // wrong method *
    // settext("changed text"); // right method *

    // Mode change color State
    const [modeColor, setModeColor] = useState({
        color: 'black',
        backgroundColor: '#d7d7d7'
    });

    // Mode change text State

    const [btnText, setBtnText] = useState("Dark Mode");

    const changeMode = () => {
        if (modeColor.backgroundColor === '#d7d7d7') {
            setModeColor({
                color: 'white',
                backgroundColor: 'black'
            });
            setBtnText("Light Mode");
        } else {
            setModeColor({
                color: 'black',
                backgroundColor: '#d7d7d7'
            });
            setBtnText("Dark Mode");
        }
    }

    // Copy State
    const [copyText, setCopyText] = useState("Copy");

    const copyTextFun = () => {
        navigator.clipboard.writeText(text);
        setCopyText("Copied");
    }

    // Undo Redo State
    const undoRedoReducer = (state, action) => {
        switch (action.type) {
          case "UNDO":
            if (state.history.length > 0) {
              const previousState = state.history[state.history.length - 1];
              setText(previousState);
              return {
                history: state.history.slice(0, state.history.length - 1),
              };
            }
            return state;
          case "REDO":
            if (state.future.length > 0) {
              const nextState = state.future[0];
              setText(nextState);
              return {
                history: [...state.history, nextState],
                future: state.future.slice(1),
              };
            }
            return state;
          default:
            return state;
        }
      };
      const [undoRedoState, dispatchUndoRedo] = useReducer(undoRedoReducer, {
        history: [],
        future: [],
      });
    
      const undo = () => {
        dispatchUndoRedo({ type: "UNDO" });
      };
    
      const redo = () => {
        dispatchUndoRedo({ type: "REDO" });
      };
    
    return (
        <>
            <div style={modeColor} className='textFormMainDiv'>
                <div className='mt-3'>
                    <div className='d-flex justify-content-between'>
                        <h3> Enter Text below</h3>
                        <div>
                            <button className="btn btn-dark copyBtn" onClick={changeMode}>{btnText}</button>
                            <button className="btn btn-dark ms-2 copyBtn" onClick={copyTextFun}>{copyText}</button>
                        </div>
                    </div>
                    <div className="mb-3">
                        <textarea className="form-control" id="" rows="15" value={text} onChange={updateText}></textarea>
                    </div>
                    <button className="btn btn-primary" onClick={convertUpper}>Convert to UpperCase</button>
                    <button className="btn btn-primary ms-2" onClick={convertLower}>Convert to LowerCase</button>
                    <button className="btn btn-primary ms-2" onClick={removeSpace}>Remove Extra Space</button>
                    <button className="btn btn-primary ms-2" onClick={reverseText}>Inverse String</button>
                    <button className="btn btn-primary ms-2" onClick={clearText}>Clear Text</button>
                    <button className="btn btn-primary ms-2" onClick={undo}>
        Undo
      </button>
      <button className="btn btn-primary ms-2" onClick={redo}>
        Redo
      </button>
                </div>

                <div className="mt-3">
                    <h3>
                        Text Details
                    </h3>

                    {(() => {
                        if (text.length === 0) {
                            return <><p> <b> 0 </b> word and <b> {text.length}</b> character </p>
                                <p> <b> 0 </b> Minute take time to read </p>
                            </>
                        }
                        else {
                            const wordcount = text.split(/\s+/).filter(word => word !== "").length;
                            return <>  <p> <b>{wordcount} </b> word and <b> {text.length}</b> character </p>
                                <p> <b>{(0.008 * wordcount).toFixed(2)}</b> Minute take time to read </p> </>
                        }
                    })()}

                    <h3>Preview</h3>
                    {/* Ternery operator*/}
                    {/* <div>
                    {text.length === 0 ? ( <p> No Text </p>) : ( <p> {text}</p>)}
                </div> */}

                    {/* if-else text available or not  */}
                    {(() => {
                        if (text.length === 0) {
                            return <p> No Text</p>
                        }
                        else {
                            return <p> {text}</p> 
                            // return <p> <pre> {text} </pre></p> 
                        }
                    })()}
                </div>
            </div>
        </>
    )
}
