import React, { useState } from 'react'

export default function TextForm() {

    const convertUpper = () => {

        let newtext = text.toUpperCase();
        setText(newtext);
        
    }

    const updateText = (event) =>
    {
        setText(event.target.value);
    }

    const [text, setText] = useState("Enetr text"); // State used this
    // text = "change text";   // wrong method *
    // settext("changed text"); // right method * 
    return (
        <>
        <div>
            <div className="mb-3">
                <h3> Enter Text below</h3>
                <textarea className="form-control" id="" rows="15" value={text} onChange={updateText}></textarea>
            </div>
            <button className="btn btn-primary" onClick={convertUpper}>Convert UpperCase</button>
            <button className="btn btn-primary" onClick={convertLower}>Convert LowerCase</button>
        </div>
        <div className="container mt-3">
            <h3>
                Text Details
            </h3>
            <p> {text.split(" ").length} word and {text.length} character </p>
        </div>
        </>
    )
}
