import React, { useState } from 'react'

export default function TextForm() {

    const convertUpper = () => {

        let newtext = text.toUpperCase();
        setText(newtext);

    }
    const convertLower = () => {
        let newtext = text.toLowerCase();
        setText(newtext);
    }


    const updateText = (event) => {
        setText(event.target.value);
    }

    const [text, setText] = useState("Enter Text"); // State used this
    // text = "change text";   // wrong method *
    // settext("changed text"); // right method *
    return (
        <>
            <div>
                <div className="mb-3">
                    <h3> Enter Text below</h3>
                    <textarea className="form-control" id="" rows="15" value={text} onChange={updateText}></textarea>
                </div>
                <button className="btn btn-primary" onClick={convertUpper}>Convert to UpperCase</button>
                <button className="btn btn-primary ms-4" onClick={convertLower}>Convert to LowerCase</button>
            </div>
            <div className="container mt-3">
                <h3>
                    Text Details
                </h3>
                <p> {text.split(" ").length} word and {text.length} character </p>
                <p> {0.008 * text.split(" ").length} take time to read </p>
                {/* Ternery operator*/}
                {/* <div>
                    <h2>Preview</h2>
                    {text.length === 0 ? ( <p> No Text </p>) : ( <p> {text}</p>)}
                </div> */}

                {/* if-else  */}
                {(() => {
                    if(text.length === 0){
                        return <p> No Text</p>
                    }
                    else{
                        return <p>{text}</p>
                    }
                })()}
            </div>
        </>
    )
}
